package SelectiveRepeat;

import java.io.IOException;
import java.net.*;
import java.util.LinkedList;
import java.util.Queue;

public class SenderSocket{
    private static int windowSize = 8;
    private static int timeout = 50;
    private DatagramSocket datagramSocket;
    private SingleSendingThread[] window;
    private ReceivingThread receivingThread;
    private InetSocketAddress receiverAddress;
    private int startIdx;
    private int lastIdx;

    private Queue<String> responseQueue;

    public SenderSocket(int port) throws SocketException {
        datagramSocket = new DatagramSocket(port);
        window = new SingleSendingThread[windowSize];
        receivingThread = new ReceivingThread(this);
        responseQueue = new LinkedList<>();
        startIdx = 0;
        lastIdx = 0;
    }

    public InetSocketAddress getReceiverAddress() {
        return receiverAddress;
    }

    private void udpSend(Packet packet) throws IOException {
        byte[] packetBytes = packet.generate();
        DatagramPacket dpacket = new DatagramPacket(packetBytes, packetBytes.length, InetAddress.getByName(Config.getRouterAddress()), Config.getRouterPort());
        datagramSocket.send(dpacket);
    }

    private Packet udpReceive() throws IOException {
        byte[] buffer = new byte[1024];
        DatagramPacket datagramPacket = new DatagramPacket(buffer, buffer.length);
        datagramSocket.receive(datagramPacket);
        return new Packet(datagramPacket.getData());
    }

    public String accept() throws IOException {
        while (true) {
            Packet p = udpReceive();
            if (Config.isFirstHandshakePacket(p)) {
                InetSocketAddress address = p.getInetAddress();
                Thread singleSendingThread = new SingleSendingThread(1, "Hi", 0 , this, address);
                singleSendingThread.start();
                Packet thirdHandshake = udpReceive();
                if (thirdHandshake.isAck() && thirdHandshake.getSeqNum() == 2 && address.equals(thirdHandshake.getInetAddress())) {
                    receiverAddress = address;
                    singleSendingThread.interrupt();
                    return thirdHandshake.getData();
                }
            }
        }
    }

    public void send(String rep) {
        for (int i = 0; i < rep.length(); i += 250) {
            if (rep.length() < i + 250) {
                responseQueue.add(rep.substring(i, rep.length()));
            } else {
                responseQueue.add(rep.substring(i, i + 250));
            }
        }
        for (int i = 0; i < windowSize; i++) {
            if (!responseQueue.isEmpty()) {
                String data = responseQueue.remove();
                int seqNum = (responseQueue.isEmpty()) ? 4: 3;
                window[i] = new SingleSendingThread(seqNum, data, lastIdx, this, receiverAddress);
                window[i].start();
                lastIdx++;
            } else {
                break;
            }
        }
        receivingThread.start();
    }

    private void closeAll() {
        for (int i = 0; i < windowSize; i++) {
            if (window[i] != null) {
                window[i].interrupt();
                window[i] = null;
            }
        }
    }

    private void handleResponse(Packet p) {
        if (p.getSeqNum() < 3) return;
        if (p.isAck()) {
            // System.out.println(p);
            int packetNumber = p.getPacketNum();
            if (packetNumber < startIdx || packetNumber - startIdx >= windowSize || window[packetNumber - startIdx] == null) {
                return;
            } else {
                window[packetNumber - startIdx].interrupt();
                window[packetNumber - startIdx] = null;
                int i = 0;
                for (; i < windowSize; i++) {
                    if (window[i] != null) break;
                }
                for (int j = 0; j < windowSize; j++) {
                    if (j < windowSize - i) {
                        window[j] = window[j + i];
                    } else {
                        window[j] = null;
                    }
                }
                startIdx += i;
                while (lastIdx - startIdx < windowSize && !responseQueue.isEmpty()) {
                    String data = responseQueue.remove();
                    int seqNum = (responseQueue.isEmpty()) ? 4: 3;
                    window[lastIdx - startIdx] = new SingleSendingThread(seqNum, data, lastIdx, this, receiverAddress);
                    window[lastIdx - startIdx].start();
                    lastIdx++;
                }
            }
        }
    }

    public class ReceivingThread extends Thread {
        private SenderSocket socket;

        public ReceivingThread(SenderSocket socket) {
            this.socket = socket;
        }

        @Override
        public void run() {
            try {
                socket.datagramSocket.setSoTimeout(5000);
            } catch (SocketException e) {
                throw new RuntimeException(e);
            }
            while (true) {
                try {
                    Packet p = udpReceive();
                    // System.out.println(p);
                    if (p.getSeqNum() >= 3) {
                        socket.handleResponse(p);
                        if (p.getSeqNum() == 4) {
                            socket.receivingThread.interrupt();
                            break;
                        }
                    }
                } catch (SocketTimeoutException e) {
                    socket.closeAll();
                    break;
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
        }
    }

    public class SingleSendingThread extends Thread {
        private int seqNum;
        private String data;
        private int packetNumber;

        private SenderSocket socket;

        private InetSocketAddress address;

        public SingleSendingThread(int seqNum, String data, int packetNumber, SenderSocket socket, InetSocketAddress address) {
            this.seqNum = seqNum;
            this.data = data;
            this.packetNumber = packetNumber;
            this.socket = socket;
            this.address = address;
        }

        @Override
        public void run() {
            boolean stop = false;
            while(!stop) {
                try {
                    udpSend(new Packet(seqNum, packetNumber,true, address, data));
                    Thread.sleep(timeout);
                    // System.out.println(packetNumber);
                } catch (IOException e) {
                    throw new RuntimeException(e);
                } catch (InterruptedException e) {
                    stop = true;
                }
            }

        }
    }

    public static void main(String[] args) {
        try {
            SenderSocket socket = new SenderSocket(8007);
            String s = socket.accept();
            socket.send("");
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}
